SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_hemodialise_atual ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* ie_opcao_p
   U - Última
   A - Atual
*/
qt_registro_w		integer;
nr_dialise_w		bigint;


BEGIN

/* Verifica se o paciente tem alguma hemodiálise gerada */

begin
select	count(*)
into STRICT	qt_registro_w
from	hd_dialise
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
exception
	when others then
	qt_registro_w		:= 0;
end;

/* Verifica se o paciente tem alguma hemodiálise ativa */

if (qt_registro_w > 0) then

	if (ie_opcao_p = 'U') then
		begin
		select	max(nr_sequencia)
		into STRICT	nr_dialise_w
		from	hd_dialise
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
		exception
			when others then
			nr_dialise_w	:= 0;
		end;
	elsif (ie_opcao_p = 'A') then
		begin
		select	nr_sequencia
		into STRICT	nr_dialise_w
		from	hd_dialise
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	coalesce(dt_fim_dialise::text, '') = ''
		and	coalesce(dt_cancelamento::text, '') = '';
		exception
			when others then
			nr_dialise_w	:= 0;
		end;
	end if;
else
	nr_dialise_w	:= 0;
end if;

return nr_dialise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
