CREATE OR REPLACE FUNCTION hd_obter_hora_sessao (cd_pessoa_fisica_p text, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_hora_sessao_w integer(10);
nr_prescricao_w integer(10);


BEGIN
	select	max((b.qt_hora_sessao * 60) + b.qt_min_sessao), max(a.nr_prescricao)
		into STRICT qt_hora_sessao_w, nr_prescricao_w
	from	prescr_medica a,
		hd_prescricao b 
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	b.nr_prescricao = a.nr_prescricao 
	and	trunc(a.dt_prescricao) = trunc(dt_agenda_p);
	
	RETURN	qt_hora_sessao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
