CREATE OR REPLACE FUNCTION hd_obter_inicio_eslaca_dia (nr_seq_escala_dia_p bigint, nr_seq_escala_dialise_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;

BEGIN
if (nr_seq_escala_dia_p IS NOT NULL AND nr_seq_escala_dia_p::text <> '') then
	select	max(DT_INICIO_ESCALA_DIA)
	into STRICT	dt_retorno_w
	from	hd_escala_dialise_dia
	where	nr_sequencia = nr_seq_escala_dia_p;
end if;

if	((coalesce(dt_retorno_w::text, '') = '') and (nr_seq_escala_dialise_p IS NOT NULL AND nr_seq_escala_dialise_p::text <> '')) then
	select	max(dt_inicio)
	into STRICT	dt_retorno_w
	from	hd_escala_dialise
	where	nr_sequencia 	= nr_seq_escala_dialise_p;
end if;


return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
