SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_max_dialisador_pac ( cd_pessoa_fisica_p text, seq_grid_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_dializador_w	bigint;
dt_referencia_w		timestamp;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select 	dt_referencia
	into STRICT	dt_referencia_w
	from	hd_exame_grid_controle
	where 	nr_sequencia = seq_grid_p;

	select	max(x.nr_capilar_prc)
	into STRICT	nr_seq_dializador_w
	from	hd_dializador x
	where	coalesce(x.dt_descarte::text, '') = ''
	and	x.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	x.dt_definido_paciente <= dt_referencia_w;

end if;

return	nr_seq_dializador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
