CREATE OR REPLACE FUNCTION hd_obter_media_pressao ( cd_pessoa_fisica_p text, dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint, nr_seq_unid_dialise_p bigint, ie_tipo_pressao_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(255);
vl_pressao_pre_med_w	varchar(255);
vl_pressao_pos_med_w	varchar(255);

/* Tipo pressao
PRE
POS
*/
BEGIN

vl_retorno_w	:= null;

select	round(avg(d.qt_pa_sist_pre_deitado)) || ' / ' || round(avg(d.qt_pa_diast_pre_deitado)),
	round(avg(d.qt_pa_sist_pos_deitado)) || ' / ' || round(avg(d.qt_pa_diast_pos_deitado))
into STRICT	vl_pressao_pre_med_w,
	vl_pressao_pos_med_w
from	hd_pac_renal_cronico p,
	hd_unidade_dialise u,
	hd_dialise d
where	u.nr_sequencia = d.nr_seq_unid_dialise
and	p.cd_pessoa_fisica = d.cd_pessoa_fisica
and	coalesce(p.dt_fim::text, '') = ''
and	coalesce(d.dt_cancelamento::text, '') = ''
and	(d.qt_pa_sist_pre_deitado IS NOT NULL AND d.qt_pa_sist_pre_deitado::text <> '')
and	substr(hd_obter_se_paciente_ativo(p.cd_pessoa_fisica),1,1) = 'S'
and	p.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	d.dt_dialise between dt_inicial_p and fim_dia(dt_final_p)
and	((u.cd_estabelecimento	= cd_estabelecimento_p) or (cd_estabelecimento_p = '0'))
and	((u.nr_sequencia	= nr_seq_unid_dialise_p) or (nr_seq_unid_dialise_p = '0'))
and	coalesce(d.nr_seq_motivo_pa_deitado_pre::text, '') = ''
and	coalesce(d.nr_seq_motivo_pa_deitado_pos::text, '') = '';

if (ie_tipo_pressao_p = 'PRE') and (vl_pressao_pre_med_w <> ' / ') then
	vl_retorno_w	:= vl_pressao_pre_med_w;
elsif (ie_tipo_pressao_p = 'POS') and (vl_pressao_pos_med_w <> ' / ') then
	vl_retorno_w	:= vl_pressao_pos_med_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
