CREATE OR REPLACE FUNCTION hd_obter_medic_ext_susp (nr_seq_medic_uso_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
ie_param_226_w	varchar(1);

BEGIN
ie_param_226_w := obter_param_usuario(7009, 226, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_226_w);

if (ie_param_226_w = 'N') or
	(ie_param_226_w = 'B' AND ie_opcao_p = 'A') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	medic_uso_continuo
	where	nr_sequencia = nr_seq_medic_uso_p
	and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '');
end if;

return	ie_retorno_w;

end;
$body$
