CREATE OR REPLACE FUNCTION hd_obter_motivo_man_maquina (nr_seq_motivo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_motivo_manutencao_w	varchar(255);
ds_retorno_w		varchar(255);


BEGIN


if (coalesce(nr_seq_motivo_p,0) > 0) then

	select 	ds_motivo_manutencao
	into STRICT	ds_motivo_manutencao_w
	from 	hd_motivo_maq_manutencao
	where	nr_sequencia = nr_seq_motivo_p;

end if;

if (ie_opcao_p = 'D') then

	ds_retorno_w	:= ds_motivo_manutencao_w;

end if;


return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
