CREATE OR REPLACE FUNCTION hd_obter_nr_dia_mes_pf ( cd_pessoa_fisica_p text, nr_opcao_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nr_seq_exame_item_p bigint default null) RETURNS bigint AS $body$
DECLARE

			
ds_retorno_w		bigint;
ie_inicio_dialise_w	varchar(5);


BEGIN

select	coalesce(max(ie_inicio_dialise),'N')
into STRICT	ie_inicio_dialise_w
from	hd_prot_exame_padrao_item
where	nr_sequencia = nr_seq_exame_item_p;

if (ie_opcao_p = 'D') then
	
	if (ie_inicio_dialise_w = 'S') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

		select	(to_char(dt_inicio_tratamento, 'dd'))::numeric 
		into STRICT	ds_retorno_w
		from	hd_pac_renal_cronico
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
		
	else
		ds_retorno_w := nr_opcao_p;
		
	end if;	
		
elsif (ie_opcao_p = 'M') then

	if (ie_inicio_dialise_w = 'S') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

		select	(to_char(dt_inicio_tratamento, 'mm'))::numeric
		into STRICT	ds_retorno_w
		from	hd_pac_renal_cronico
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
		
	else

		ds_retorno_w := nr_opcao_p;
	
	end if;
	
elsif (ie_opcao_p = 'A') then	
	
	 if 	(ie_inicio_dialise_w = 'S' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		
		select	(to_char(dt_inicio_tratamento, 'yyyy'))::numeric 
		into STRICT	ds_retorno_w
		from	hd_pac_renal_cronico
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

		if (coalesce(ds_retorno_w::text, '') = '') then
		
			select 	(to_char(min(dt_inicio_tratamento), 'yyyy'))::numeric
			into STRICT	ds_retorno_w
			from	paciente_tratamento
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
			
			if (coalesce(ds_retorno_w::text, '') = '') then
			
				select	(to_char(dt_atualizacao_nrec, 'yyyy'))::numeric
				into STRICT	ds_retorno_w
				from	hd_pac_renal_cronico
				where	cd_pessoa_fisica = cd_pessoa_fisica_p;				
			
			end if;
		
		end if;
	
	end if;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
