SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_pac_transito ( nr_seq_unid_dialise_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



qt_retorno_w		bigint;
qt_externo_w		bigint;

/* Opção
PE = Pacientes em hemodialise
PT = Pacientes em trânsito interno
PU = Pacientes em trânsito externo */
BEGIN

if (nr_seq_unid_dialise_p IS NOT NULL AND nr_seq_unid_dialise_p::text <> '') then
	begin

	if (ie_opcao_p = 'PE') then

		select	count(*)
		into STRICT	qt_retorno_w
		from	hd_unidade_dialise u,
			hd_pac_renal_cronico p
		where	u.nr_sequencia = p.nr_seq_unid_dialise
		and	substr(hd_obter_se_paciente_ativo(cd_pessoa_fisica),1,1) = 'S'
		and	u.ie_propria = 'S'
		and (p.ie_paciente_transito = 'N')
		and	p.nr_seq_unid_dialise = nr_seq_unid_dialise_p
		and	substr(hd_obter_unidade_prc(cd_pessoa_fisica,'C'),1,80) = nr_seq_unid_dialise_p;

	elsif (ie_opcao_p = 'PT') then
		begin

		select	count(*)
		into STRICT	qt_retorno_w
		from	hd_unidade_dialise u,
			hd_pac_renal_cronico p
		where	u.nr_sequencia = p.nr_seq_unid_dialise
		and	substr(hd_obter_se_paciente_ativo(cd_pessoa_fisica),1,1) = 'S'
		and	u.ie_propria = 'S'
		and (p.ie_paciente_transito = 'N')
		and (p.nr_seq_unid_dialise <> nr_seq_unid_dialise_p)
		and	substr(hd_obter_unidade_prc(cd_pessoa_fisica,'C'),1,80) = nr_seq_unid_dialise_p;

		select	count(*)
		into STRICT	qt_externo_w
		from	hd_unidade_dialise u,
			hd_pac_renal_cronico p
		where	u.nr_sequencia = p.nr_seq_unid_dialise
		and	substr(hd_obter_se_paciente_ativo(cd_pessoa_fisica),1,1) = 'S'
		and	u.ie_propria = 'S'
		and	p.ie_paciente_transito = 'S'
		and	substr(hd_obter_unidade_prc(cd_pessoa_fisica,'C'),1,80) = nr_seq_unid_dialise_p;

		select	qt_retorno_w + qt_externo_w
		into STRICT	qt_retorno_w
		;

		end;

	elsif (ie_opcao_p = 'PU') then

		select	count(*)
		into STRICT	qt_retorno_w
		from	hd_unidade_dialise u,
			hd_pac_renal_cronico p
		where	u.nr_sequencia = p.nr_seq_unid_dialise
		and	substr(hd_obter_se_paciente_ativo(cd_pessoa_fisica),1,1) = 'S'
		and	u.ie_propria = 'S'
		and	p.nr_seq_unid_dialise = nr_seq_unid_dialise_p
		and	p.nr_seq_unid_dialise <> substr(hd_obter_unidade_prc(cd_pessoa_fisica,'C'),1,80);

	end if;

	end;
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
