CREATE OR REPLACE FUNCTION hd_obter_peso_ideal_prescr (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

qt_retorno_w	real;

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select   max(b.qt_peso_ideal)
	into STRICT	qt_retorno_w
	from     prescr_medica a,
	         hd_prescricao b
	where    a.nr_prescricao = b.nr_prescricao
	and	(b.qt_peso_ideal IS NOT NULL AND b.qt_peso_ideal::text <> '')
	and      a.nr_prescricao = (SELECT	max(nr_prescricao)
				from    	prescr_medica c
			         where    ((c.ie_hemodialise = 'E' and trunc(c.dt_prescricao)= trunc(clock_timestamp())) or (c.ie_hemodialise = 'S'))
			         and    	c.cd_pessoa_fisica    = cd_pessoa_fisica_p
			         and    	(coalesce(c.dt_liberacao_medico, c.dt_liberacao) IS NOT NULL AND (coalesce(c.dt_liberacao_medico, c.dt_liberacao))::text <> '')
			         and    	coalesce(c.dt_fim_prescricao::text, '') = '');

	if (coalesce(qt_retorno_w::text, '') = '')	then
		select	max(qt_peso_ideal)
		into STRICT	qt_retorno_w
		from	hd_pac_renal_cronico
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
end if;
return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
