CREATE OR REPLACE FUNCTION hd_obter_peso_pos_data (cd_pessoa_fisica_p text, seq_grid_p bigint) RETURNS bigint AS $body$
DECLARE


peso_pos_w		double precision;
dt_referencia_w		timestamp;

BEGIN

select 	dt_referencia
into STRICT	dt_referencia_w
from	HD_EXAME_GRID_CONTROLE
where 	nr_sequencia = seq_grid_p;

select 	round((sum(qt_peso_pos)/count(*))::numeric,2)
into STRICT	peso_pos_w
from	hd_dialise
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	qt_peso_pos > 0
and	trunc(dt_dialise,'month') = trunc(dt_referencia_w,'month');

return peso_pos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
