CREATE OR REPLACE FUNCTION hd_obter_procedimentos_exames (nr_seq_proc_p bigint, nr_sequencia_p bigint, cd_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE



ds_procedimento_exame_w		varchar(255);


BEGIN

select
	max(coalesce(substr(obter_desc_proc_interno(a.nr_seq_proc_interno),1,100),
	substr(coalesce(obter_desc_estrut_proc(null,null,null,a.cd_procedimento,a.ie_origem_proced),obter_desc_exame(a.nr_seq_exame)),1,200))) ds
into STRICT ds_procedimento_exame_w
from	protocolo_medic_proc a
where a.nr_sequencia = nr_sequencia_p
and	a.nr_seq_proc = nr_seq_proc_p
and	a.cd_protocolo = cd_protocolo_p;

return ds_procedimento_exame_w;

end;
$body$
