CREATE OR REPLACE FUNCTION hd_obter_profissional_paciente ( cd_pessoa_fisica_p bigint, ie_tipo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
ie_tipo_p 
	'M' => Médico 
	'E' => Enfermeiro 
 
ie_opcao_p 
	'C' => Código 
	'N' => Nome 
*/
 
 
 
ds_retorno_w		varchar(100);	
cd_profissional_w	varchar(20);
nm_profissional_w	varchar(100);

 
C01 CURSOR FOR 
SELECT	cd_profissional, 
	substr(obter_nome_pf(cd_profissional),1,50) 
from	hd_profissional_resp 
where	ie_tipo_profissional	= ie_tipo_p 
and	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	coalesce(dt_fim::text, '') = '' 
order by 
	dt_inicio;


BEGIN 
 
ds_retorno_w := '';
 
open C01;
	loop	 
		fetch C01 into 
			cd_profissional_w, 
			nm_profissional_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;	
 
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w := cd_profissional_w;
else 
	ds_retorno_w := nm_profissional_w;
end if;	
 
 
 
return	ds_retorno_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION hd_obter_profissional_paciente ( cd_pessoa_fisica_p bigint, ie_tipo_p text, ie_opcao_p text) FROM PUBLIC;

