CREATE OR REPLACE FUNCTION hd_obter_protocolo_tipo_sol (cd_pessoa_fisica_p text, ie_tipo_solucao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_protocolo_w				varchar(60);
ds_retorno_w				varchar(255);
nr_prescricao_w				bigint;
nr_seq_solucao_w				integer;

C01 CURSOR FOR 
	SELECT	a.nr_seq_solucao 
	from	prescr_solucao a 
	where	a.nr_prescricao = nr_prescricao_w 
	and	a.ie_tipo_solucao = ie_tipo_solucao_p;


BEGIN 
 
select	max(a.nr_prescricao) 
into STRICT	nr_prescricao_w 
from	prescr_solucao a, 
	prescr_medica b, 
	atendimento_paciente c 
where	a.nr_prescricao = b.nr_prescricao 
and	c.nr_atendimento = b.nr_atendimento 
and	c.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and	a.ie_tipo_solucao = ie_tipo_solucao_p;
 
if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_solucao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	substr(obter_desc_prot_npt(nr_seq_protocolo),1,60) 
	into STRICT	ds_protocolo_w 
	from	prescr_solucao 
	where	nr_prescricao = nr_prescricao_w 
	and	nr_seq_solucao = nr_seq_solucao_w;
 
	if (ds_protocolo_w IS NOT NULL AND ds_protocolo_w::text <> '') then 
 
		if (coalesce(ds_retorno_w::text, '') = '') then 
			ds_retorno_w	:= ds_protocolo_w;
		else 
			ds_retorno_w	:= substr(ds_protocolo_w||', '||ds_retorno_w,1,255);
		end if;
	end if;
	end;
	end loop;
	close C01;
end if;	
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
