CREATE OR REPLACE FUNCTION hd_obter_prox_dia_semana (dt_referencia_p timestamp, ie_dia_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_dia_semana_w			timestamp;
ie_dia_semana_w			integer;
qt_dif_data_w			integer := 0;

/*ie_dia_p
1, 'Domingo',
2, 'Segunda-Feira',
3, 'Terça-Feira',
4, 'Quarta-Feira',
5, 'Quinta-Feira',
6, 'Sexta-Feira',
7, 'Sábado'*/
BEGIN

select PKG_DATE_UTILS.get_WeekDay(dt_referencia_p)
into STRICT ie_dia_semana_w
;

if (ie_dia_p = ie_dia_semana_w) then
	ds_dia_semana_w := dt_referencia_p;
else
	qt_dif_data_w := ie_dia_p - ie_dia_semana_w;
	if (qt_dif_data_w > 0) then
		ds_dia_semana_w := dt_referencia_p + qt_dif_data_w;
	end if;
end if;

return ds_dia_semana_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
