CREATE OR REPLACE FUNCTION hd_obter_qt_baixa_medic (cd_pessoa_fisica_p text, nr_seq_medic_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_ignorar_datas_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_baixa_w	bigint;


BEGIN

select	sum(qt_medicamento)
into STRICT	qt_baixa_w
from	hd_dialise_med_ext
where	ie_local = 'P'
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	nr_seq_medic = nr_seq_medic_p
and	((ie_ignorar_datas_p = 'S')
	or ((dt_atualizacao_nrec between dt_inicial_p and fim_dia(dt_final_p))
		and (ie_ignorar_datas_p = 'N')))
and 	((cd_estabelecimento = cd_estabelecimento_p) or (cd_estabelecimento_p = '0'));

return	qt_baixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
