CREATE OR REPLACE FUNCTION hd_obter_qt_dialise ( nr_seq_unidade_p bigint, cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_dialise_w	bigint;

/* Opção:
F - Dialises finalizadas
C - Dialises canceladas
*/
BEGIN

if (ie_opcao_p = 'F') then
	select	count(*)
	into STRICT	qt_dialise_w
	from	hd_dialise
	where	(dt_fim_dialise IS NOT NULL AND dt_fim_dialise::text <> '')
	and	coalesce(dt_cancelamento::text, '') = ''
	and (nr_seq_unid_dialise = nr_seq_unidade_p or nr_seq_unidade_p = 0)
	and (hd_obter_estab_unidade(nr_seq_unid_dialise) = cd_estabelecimento_p or cd_estabelecimento_p = 0)
	and	dt_dialise between dt_inicial_p and fim_dia(dt_final_p);
elsif (ie_opcao_p = 'C') then
	select	count(*)
	into STRICT	qt_dialise_w
	from	hd_dialise
	where	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '')
	and	coalesce(dt_fim_dialise::text, '') = ''
	and (nr_seq_unid_dialise = nr_seq_unidade_p or nr_seq_unidade_p = 0)
	and (hd_obter_estab_unidade(nr_seq_unid_dialise) = cd_estabelecimento_p or cd_estabelecimento_p = 0)
	and	dt_dialise between dt_inicial_p and fim_dia(dt_final_p);
end if;

return qt_dialise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
