CREATE OR REPLACE FUNCTION hd_obter_qt_med_utilizado ( cd_pessoa_fisica_p text, nr_seq_medic_p bigint) RETURNS bigint AS $body$
DECLARE

 
ds_retorno_w		bigint;			

BEGIN 
ds_retorno_w	:= 0;
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	select 	coalesce(sum(coalesce(qt_medicamento,0)),0) 
	into STRICT	ds_retorno_w 
	from	hd_dialise_med_ext 
	where	((hd_obter_dados_dialise(nr_seq_dialise,'PF') = cd_pessoa_fisica_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p)) 
	and	nr_seq_medic = nr_seq_medic_p;
 
		 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
