CREATE OR REPLACE FUNCTION hd_obter_qt_pac_agudo ( cd_estabelecimento_p bigint, nr_seq_unid_dialise_p bigint, dt_inicio_trat_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
A - Agudo
C - Cr√¥nico
*/
qt_pac_agudo_w		bigint;


BEGIN

if (ie_opcao_p = 'A') then
	select	count(*)
	into STRICT	qt_pac_agudo_w
	from (	SELECT	distinct a.cd_pessoa_fisica
			from	paciente_tratamento a
			where	coalesce(a.dt_final_tratamento::text, '') = ''
			and	trunc(a.dt_inicio_tratamento,'mm') = dt_inicio_trat_p
			and	hd_obter_se_paciente_ativo(a.cd_pessoa_fisica) = 'S'
			and	exists (select 1 from hd_pac_renal_cronico b
					where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
					and	b.ie_paciente_agudo = 'S'
					and (b.cd_estabelecimento = cd_estabelecimento_p or cd_estabelecimento_p = 0)
					and (hd_obter_unidade_prc(b.cd_pessoa_fisica,'C') = nr_seq_unid_dialise_p or nr_seq_unid_dialise_p = 0))) alias9;
elsif (ie_opcao_p = 'C') then
	select	count(*)
	into STRICT	qt_pac_agudo_w
	from (	SELECT	distinct a.cd_pessoa_fisica
			from	paciente_tratamento a
			where	coalesce(a.dt_final_tratamento::text, '') = ''
			and	trunc(a.dt_inicio_tratamento,'mm') = dt_inicio_trat_p
			and	hd_obter_se_paciente_ativo(a.cd_pessoa_fisica) = 'S'
			and	exists (select 1 from hd_pac_renal_cronico b
					where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
					and	b.ie_paciente_agudo = 'N'
					and (b.cd_estabelecimento = cd_estabelecimento_p or cd_estabelecimento_p = 0)
					and (hd_obter_unidade_prc(b.cd_pessoa_fisica,'C') = nr_seq_unid_dialise_p or nr_seq_unid_dialise_p = 0))) alias9;
end if;

return qt_pac_agudo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
