CREATE OR REPLACE FUNCTION hd_obter_qt_saldo_medic (nr_seq_entrega_p bigint, nr_seq_medic_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_mat_entregue_w	bigint;
qt_adm_w		bigint;
qt_baixas_w		bigint;
qt_saldo_W		bigint;


BEGIN

/*select	nvl(sum(to_number(hd_obter_qt_mat_entregue(nr_seq_entrega_p, sysdate, sysdate, 'S'))),0)
into	qt_mat_entregue_w
from	dual;*/
select	coalesce(sum(c.qt_material),0) qt_medic
into STRICT	qt_mat_entregue_w
from	hd_entrega_medic b,
	hd_lote_paciente c
where	b.nr_seq_medic = nr_seq_medic_p
and	c.nr_seq_entrega = b.nr_sequencia
and	c.cd_pessoa_fisica = cd_pessoa_fisica_p
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and 	((b.cd_estabelecimento = cd_estabelecimento_p) or (cd_estabelecimento_p = '0'))
AND (ie_controlado_hosp = 'S' OR (coalesce(ie_controlado_hosp::text, '') = ''))
order by 1;


select	coalesce(max((hd_obter_qt_adm_medic(cd_pessoa_fisica_p, nr_seq_medic_p, clock_timestamp(), clock_timestamp(), 'S',cd_estabelecimento_p))::numeric ),0)
into STRICT	qt_adm_w
;

select	coalesce(max((hd_obter_qt_baixa_medic(cd_pessoa_fisica_p, nr_seq_medic_p, clock_timestamp(), clock_timestamp(), 'S',cd_estabelecimento_p))::numeric ),0)
into STRICT	qt_baixas_w
;

select	((qt_mat_entregue_w - qt_adm_w) - qt_baixas_w)
into STRICT	qt_saldo_W
;

return	qt_saldo_W;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
