CREATE OR REPLACE FUNCTION hd_obter_qt_tratamento ( nr_seq_unidade_p bigint, cd_pessoa_fisica_p text, ie_transito_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_tratamento_w		bigint;
qt_1_w			integer;
qt_2_w			integer;
qt_3_w			integer;
qt_4_w			integer;
qt_total_w		integer;
/*
Opcao: Tipo de tratamento

CO   = Conservador
CAPD = Dialise Peritoneal Ambulatorial Continua
DPA  = Dialise Peritoneal Automatizada
CPI  = Dialise Peritoneal Intermitente
HD   = Hemodialise
PT   = Pos-Transplante
TX   = Pre-Transplante
TR   = Transplante

*/
BEGIN

/* Total de tratamentos por tipo */

if (coalesce(cd_pessoa_fisica_p::text, '') = '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

select	count(*)
into STRICT	qt_1_w
from	paciente_tratamento a
where	coalesce(a.dt_final_tratamento::text, '') = ''
and	substr(HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'),1,200) = nr_seq_unidade_p
and	a.ie_tratamento = ie_opcao_p
and	ie_opcao_p in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	(((ie_transito_p = 'N') and (substr(hd_obter_se_pac_transito(a.cd_pessoa_fisica),1,1) = 'N') and (hd_obter_unid_paciente(a.cd_pessoa_fisica) = HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'))) or (ie_transito_p = 'S'));
			
select	count(*)
into STRICT	qt_2_w
from	paciente_tratamento a
where	coalesce(a.dt_final_tratamento::text, '') = ''
and	substr(HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'),1,200) = nr_seq_unidade_p
and	a.ie_tratamento = ie_opcao_p
and	ie_opcao_p not in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	(((ie_transito_p = 'N') and (substr(hd_obter_se_pac_transito(a.cd_pessoa_fisica),1,1) = 'N') and (hd_obter_unid_paciente(a.cd_pessoa_fisica) = HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'))) or (ie_transito_p = 'S'));

qt_total_w := qt_1_w+qt_2_w;

/* Total de tratamentos por paciente */

elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

select	count(*)
into STRICT	qt_3_w
from	paciente_tratamento a
where	coalesce(a.dt_final_tratamento::text, '') = ''
and	a.ie_tratamento = ie_opcao_p
and	ie_opcao_p not in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	substr(HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'),1,200) = nr_seq_unidade_p
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	(((ie_transito_p = 'N') and (substr(hd_obter_se_pac_transito(a.cd_pessoa_fisica),1,1) = 'N') and (hd_obter_unid_paciente(a.cd_pessoa_fisica) = HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'))) or (ie_transito_p = 'S'));

select	count(*)
into STRICT	qt_4_w
from	paciente_tratamento a
where	coalesce(a.dt_final_tratamento::text, '') = ''
and	a.ie_tratamento = ie_opcao_p
and	ie_opcao_p in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	substr(HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'),1,200) = nr_seq_unidade_p
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	(((ie_transito_p = 'N') and (substr(hd_obter_se_pac_transito(a.cd_pessoa_fisica),1,1) = 'N') and (hd_obter_unid_paciente(a.cd_pessoa_fisica) = HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'))) or (ie_transito_p = 'S'));

qt_total_w := qt_3_w+qt_4_w;	
		
end if;

qt_tratamento_w := qt_total_w;

return qt_tratamento_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION hd_obter_qt_tratamento ( nr_seq_unidade_p bigint, cd_pessoa_fisica_p text, ie_transito_p text, ie_opcao_p text) FROM PUBLIC;

