SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_qt_trat_pac ( nr_seq_unidade_p bigint, cd_pessoa_fisica_p text, ie_transito_p text) RETURNS bigint AS $body$
DECLARE


qt_tratamento_w		bigint;
qt_trat_receptor_w		bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_tratamento_w
	from	paciente_tratamento a
	where	coalesce(a.dt_final_tratamento::text, '') = ''
	and	substr(HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'),1,200) = nr_seq_unidade_p
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(((ie_transito_p = 'N') and (substr(hd_obter_se_pac_transito(a.cd_pessoa_fisica),1,1) = 'N') and (hd_obter_unid_paciente(a.cd_pessoa_fisica) = HD_Obter_Unidade_PRC(a.cd_pessoa_fisica,'C'))) or (ie_transito_p = 'S'));

	if (qt_tratamento_w > 1) then

		select	count(*)
		into STRICT	qt_trat_receptor_w
		from	paciente_tratamento d,
			tx_receptor c,
			tx_registro_snt b,
			tx_central_transpl a
		where	a.nr_sequencia		= b.nr_seq_central
		and	b.nr_seq_receptor	= c.nr_sequencia
		and	c.cd_pessoa_fisica	= d.cd_pessoa_fisica
		and	c.nr_sequencia		= d.nr_seq_receptor
		and	d.ie_tratamento 	in ('PT','TX','TR')
		and	d.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	(d.nr_seq_receptor IS NOT NULL AND d.nr_seq_receptor::text <> '')
		and	a.cd_estabelecimento	= HD_OBTER_ESTAB_UNIDADE(nr_seq_unidade_p)
		and	coalesce(d.dt_final_tratamento::text, '') = ''
		and	substr(tx_obter_se_paciente_ativo(d.nr_seq_receptor),1,1) = 'S';


		if (qt_trat_receptor_w > 0) and (qt_tratamento_w <> qt_trat_receptor_w) then
			qt_tratamento_w	:= 0;
		else
			qt_tratamento_w	:= 1;
		end if;
	end if;
	end;
end if;

return qt_tratamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
