CREATE OR REPLACE FUNCTION hd_obter_regra_pront_novo (cd_pessoa_fisica_p text, nr_seq_protocolo_exc_p bigint, nr_seq_proc_medic_exc_p bigint, dt_refencia_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);
qt_itens_w	integer;

BEGIN

ds_retorno_w	:= 'S';

if (nr_seq_proc_medic_exc_p IS NOT NULL AND nr_seq_proc_medic_exc_p::text <> '' AND nr_seq_protocolo_exc_p IS NOT NULL AND nr_seq_protocolo_exc_p::text <> '') then

	select  count(*)
	into STRICT	qt_itens_w
	from	hd_protocolo_exame a,
		hd_prot_exame_padrao_item b,
		hd_prot_exame_padrao c
	where	b.nr_seq_protocolo_padrao = a.nr_seq_protocolo
	and	c.nr_sequencia = b.nr_seq_protocolo_padrao
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(obter_prox_data_ref(b.ie_forma_solic, b.nr_dia, b.nr_mes,'')) = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_refencia_p)
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	b.nr_seq_proc_medic = nr_seq_proc_medic_exc_p
	and	b.nr_seq_protocolo = nr_seq_protocolo_exc_p;
	
	if (qt_itens_w > 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return ds_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
