CREATE OR REPLACE FUNCTION hd_obter_reg_visual_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ie_permite_w	varchar(1);

BEGIN
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_permite_w 
	from	atendimento_paciente a 
	where	a.nr_atendimento = nr_atendimento_p 
	and	((not exists (	SELECT 1 from hd_regra_visual_atend y where coalesce(y.ie_situacao,'A') = 'A')) 
		or (exists (	select	1 
			from	paciente_tratamento z , 
					hd_regra_visual_atend y 
			where	a.cd_pessoa_fisica = z.cd_pessoa_fisica 
			and		a.ie_tipo_atendimento = coalesce(y.ie_tipo_atendimento,a.ie_tipo_atendimento) 
			and		(z.ie_paciente_agudo IS NOT NULL AND z.ie_paciente_agudo::text <> '') 
			and		coalesce(y.ie_situacao,'A') = 'A' 
			and		((z.ie_paciente_agudo = y.ie_tipo_paciente) or (y.ie_tipo_paciente = 'A')) 
			and		coalesce(z.dt_final_tratamento::text, '') = '')));
		 
end if;
return	ie_permite_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
