SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_reuso_dialiasdor_desc (cd_pessoa_fisica_p text, seq_grid_p bigint) RETURNS bigint AS $body$
DECLARE


qt_reuso_w		integer;
dt_referencia_w		timestamp;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select 	dt_referencia
	into STRICT	dt_referencia_w
	from	hd_exame_grid_controle
	where 	nr_sequencia = seq_grid_p;

	select	max(b.qt_reuso)
	into STRICT	qt_reuso_w
	from	hd_dializador a,
		hd_dialisador_reproc b
	where	a.nr_sequencia = b.nr_seq_dialisador
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	trunc(b.dt_inicio) = dt_referencia_w;

end if;

return	qt_reuso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
