CREATE OR REPLACE FUNCTION hd_obter_se_dial_liberado ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w		bigint;
ie_retorno_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	hd_dializador
where	ie_status		= 'O'
and	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (qt_registros_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
