SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_se_em_dialise ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*ie_opcao_p
  E - Em diálise
  C - Diálise foi cancelada
  F - Diálise foi finalizada
  I - Paciente colocado/instalado
*/
qt_registro_w		bigint;
ie_retorno_w		varchar(1) := 'N';


BEGIN

if (ie_opcao_p = 'E') then
	select	count(*)
	into STRICT	qt_registro_w
	from 	hd_dialise
	where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and 	(dt_inicio_dialise IS NOT NULL AND dt_inicio_dialise::text <> '')
	and 	coalesce(dt_fim_dialise::text, '') = ''
	and	coalesce(dt_cancelamento::text, '') = '';
elsif (ie_opcao_p = 'F') then
	select	count(*)
	into STRICT	qt_registro_w
	from 	hd_dialise
	where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and 	(dt_inicio_dialise IS NOT NULL AND dt_inicio_dialise::text <> '')
	and 	(dt_fim_dialise IS NOT NULL AND dt_fim_dialise::text <> '')
	and	coalesce(dt_cancelamento::text, '') = ''
	and	dt_fim_dialise between trunc(clock_timestamp()) and fim_dia(clock_timestamp());
elsif (ie_opcao_p = 'C') then
	select	count(*)
	into STRICT	qt_registro_w
	from 	hd_dialise
	where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '')
	and	dt_cancelamento	between trunc(clock_timestamp()) and fim_dia(clock_timestamp());
elsif (ie_opcao_p = 'I') then
	select	count(*)
	into STRICT	qt_registro_w
	from 	hd_dialise
	where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and 	(dt_instalacao IS NOT NULL AND dt_instalacao::text <> '')
	and 	coalesce(dt_fim_dialise::text, '') = ''
	and	coalesce(dt_cancelamento::text, '') = '';
end if;

if (qt_registro_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
