CREATE OR REPLACE FUNCTION hd_obter_se_faixa_etaria (cd_pessoa_fisica_p text, vl_dominio_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
obter se a pessoa pertence à faixa etária do dominio 69 
*/
				 
 
ds_retorno_w 	varchar(5);
qt_reg_w	bigint;
qt_idade_w	integer;	
	 

BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND vl_dominio_p IS NOT NULL AND vl_dominio_p::text <> '') then 
	 
	qt_idade_w := obter_dados_pf(cd_pessoa_fisica_p,'I');
	 
	select	count(*) 
	into STRICT	qt_reg_w 
	from	HD_PAC_RENAL_CRONICO	 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	obter_somente_numero(substr(vl_dominio_p ,1,3)) < qt_idade_w 
	and   qt_idade_w <= obter_somente_numero(substr(vl_dominio_p ,4,6));
	 
	if (qt_reg_w = 1)		 then 
		ds_retorno_w := 'S';
	else 
		ds_retorno_w := 'N';
	end if;
	 
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
