SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_se_gera_exame_compl ( cd_pessoa_fisica_p text, ie_regra_solic_p text, nr_seq_prot_item_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_diabetico_w		varchar(1);
dt_inicio_w		timestamp;
ie_retorno_w		varchar(1)	:= 'N';
ie_gerou_presc_w	varchar(1) := 'N';


BEGIN

if (coalesce(ie_regra_solic_p::text, '') = '') then
	ie_retorno_w	:= 'S';
else
	begin
	
	select	max(dt_inicio_tratamento)
	into STRICT	dt_inicio_w
	from	paciente_tratamento
	where	ie_tratamento		in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','CAPD','CO','CPI','DPA','PL','DPAV','DDC','IHF','IHDF')
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	coalesce(dt_final_tratamento::text, '') = '';
	
	select	ie_diabetico
	into STRICT	ie_diabetico_w
	from	hd_pac_renal_cronico
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	
	select	CASE WHEN count(1)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_gerou_presc_w
	from	hd_protocolo_exame_prescr a,
			hd_prot_exame_padrao_item b
	where	a.nr_seq_prot_item = b.nr_sequencia
	and a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and a.nr_seq_prot_item = nr_seq_prot_item_p;

	if	(ie_regra_solic_p = 'D' AND ie_diabetico_w = 'S') or
		((ie_regra_solic_p = 'T') and (ie_diabetico_w <> 'S') and (obter_dias_entre_datas(dt_inicio_w, clock_timestamp()) <= 365)) or
		((ie_regra_solic_p = 'A') and (ie_diabetico_w <> 'S') and (obter_dias_entre_datas(dt_inicio_w, clock_timestamp()) > 365)) or
		((ie_regra_solic_p = 'G') and (ie_diabetico_w = 'S') and (obter_dias_entre_datas(dt_inicio_w, clock_timestamp()) <= 365)) or
		((ie_regra_solic_p = 'R') and (ie_diabetico_w = 'S') and (obter_dias_entre_datas(dt_inicio_w, clock_timestamp()) > 365)) or
		(ie_regra_solic_p = 'P' AND ie_gerou_presc_w = 'S')then
		
		ie_retorno_w	:= 'S';

	end if;
	end;
end if;
	
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
