SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_se_gerou_prescr (ie_nova_forma_p text, cd_pessoa_fisica_p text, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_prescr_w varchar(1) := 'N';


BEGIN

IF (ie_nova_forma_p = 'S') THEN

 SELECT  CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
 INTO STRICT ie_existe_prescr_w
 FROM  hd_protocolo_exame_prescr a,
  prescr_medica b
 WHERE  a.nr_seq_protocolo = nr_seq_protocolo_p
 AND    a.cd_pessoa_fisica = cd_pessoa_fisica_p
 AND    a.nr_prescricao = b.nr_prescricao
 AND    TO_CHAR(b.dt_prescricao,'MM/YYYY') = TO_CHAR(clock_timestamp(),'MM/YYYY')
 and 	coalesce(b.dt_suspensao::text, '') = '';

ELSIF (ie_nova_forma_p = 'N') THEN

 SELECT  CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
 INTO STRICT ie_existe_prescr_w
 FROM  hd_pac_prot_exame_prescr a,
  prescr_medica b
 WHERE  a.nr_seq_prot_exame = nr_seq_protocolo_p
 AND    a.nr_prescricao = b.nr_prescricao
 AND    TO_CHAR(b.dt_prescricao,'MM/YYYY') = TO_CHAR(clock_timestamp(),'MM/YYYY')
  and 	coalesce(b.dt_suspensao::text, '') = '';
END IF;

RETURN ie_existe_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
