CREATE OR REPLACE FUNCTION hd_obter_se_internado ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
nr_atendimento_w	bigint;
ie_retorno_w		varchar(1) := 'N';


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select	coalesce(max(nr_atendimento),0) 
	into STRICT	nr_atendimento_w              
	from	atendimento_paciente a             
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	((cd_estabelecimento	= wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(wheb_usuario_pck.get_cd_estabelecimento,0) = 0)) 
	and	ie_fim_conta		<> 'F' 
	and	exists (SELECT 1 from HD_LOG_GERACAO_ATEND x where a.nr_atendimento = x.nr_atendimento_nov) 
	and	coalesce(dt_alta::text, '') = '';
	 
	if nr_atendimento_w = 0 then 
		select	max(nr_atendimento) 
		into STRICT	nr_atendimento_w              
		from	atendimento_paciente              
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	((cd_estabelecimento	= wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(wheb_usuario_pck.get_cd_estabelecimento,0) = 0)) 
		and	ie_fim_conta		<> 'F'      
		and	coalesce(dt_alta::text, '') = '';
	end if;
	 
	ie_retorno_w := Obter_se_pa_internado(nr_atendimento_w);
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
