CREATE OR REPLACE FUNCTION hd_obter_se_mostra_unidade ( nr_seq_unid_dialise_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_registro_w		smallint;

cd_setor_atendimento_w	integer;
ie_tipo_evolucao_w	varchar(3);



BEGIN

begin
select	count(*)
into STRICT	qt_registro_w
from	hd_unidade_usuario
where	nr_seq_unid_dialise	= nr_seq_unid_dialise_p;
exception
	when others then
	qt_registro_w		:= 0;
end;

if (qt_registro_w > 0) then
	begin

	select	coalesce(max(ie_tipo_evolucao),'XPT'),
		coalesce(max(cd_setor_atendimento),0)
	into STRICT	ie_tipo_evolucao_w,
		cd_setor_atendimento_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select	count(*)
	into STRICT	qt_registro_w
	from	hd_unidade_usuario
	where	nr_seq_unid_dialise = nr_seq_unid_dialise_p
	and	coalesce(nm_usuario_unid,nm_usuario_p) = nm_usuario_p
	and	coalesce(cd_setor_unid,cd_setor_atendimento_w) = cd_setor_atendimento_w
	and	coalesce(ie_tipo_evolucao,ie_tipo_evolucao_w) = ie_tipo_evolucao_w;
	exception
		when others then
		qt_registro_w		:= 0;
	end;

	if (qt_registro_w > 0)	 then
		ds_retorno_w		:= 'S';
	end if;
else
	ds_retorno_w			:= 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
