CREATE OR REPLACE FUNCTION hd_obter_se_pac_dia_turno ( cd_pessoa_fisica_p text, nr_seq_turno_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_turno_w		bigint;
ie_retorno_w		varchar(1) := 'N';



BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_turno_p IS NOT NULL AND nr_seq_turno_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	hd_escala_dialise_dia d,
		hd_escala_dialise e
	where	e.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	d.nr_seq_escala    = e.nr_sequencia
	and	d.nr_seq_turno	   = nr_seq_turno_p
	and	e.dt_inicio 	   <= clock_timestamp()
	and	coalesce(e.dt_fim::text, '') = ''
	and	coalesce(d.dt_fim_escala_dia::text, '') = '';

end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
