CREATE OR REPLACE FUNCTION hd_obter_se_pac_faltou ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_pac_faltou_w	varchar(1);


BEGIN

select	coalesce(max(ie_pac_faltou),'N')
into STRICT	ie_pac_faltou_w
from	hd_prc_chegada
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and		dt_chegada between  trunc(clock_timestamp()) and fim_dia(clock_timestamp());

return ie_pac_faltou_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
