CREATE OR REPLACE FUNCTION hd_obter_se_perm_check (nr_seq_turno_p bigint, ie_dia_semana_p text, nr_seq_unid_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, dt_inicio_p text, dt_fim_p text, nr_seq_ponto_acesso_p bigint DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


    qt_vagas_w       integer;
    qt_vagas_esp_w   integer;
    ie_vaga_esp_w    varchar(1) := 'N';
    ds_retorno_w     varchar(1) := 'S';
    qt_ocupado_w     integer;
    qt_ocupado_esp_w integer;

    c01 CURSOR FOR
        SELECT a.ie_necessita_vaga_esp
          FROM hd_classificacao_sorologia a,
               hd_paciente_classif_sor    b
         WHERE a.nr_sequencia = b.nr_seq_classificacao
           AND a.ie_situacao = 'A'
           AND b.cd_pessoa_fisica = cd_pessoa_fisica_p
           AND coalesce(b.dt_fim::text, '') = ''
         ORDER BY coalesce(ie_prioridade, 999) DESC;


BEGIN

    OPEN c01;
    LOOP
        FETCH c01
            INTO ie_vaga_esp_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */
        BEGIN
            ie_vaga_esp_w := ie_vaga_esp_w;
        END;
    END LOOP;
    CLOSE c01;

    SELECT * FROM hd_obter_qtd_perm_check(nr_seq_turno_p        => nr_seq_turno_p, ie_dia_semana_p       => ie_dia_semana_p, nr_seq_unid_p         => nr_seq_unid_p, cd_estabelecimento_p  => cd_estabelecimento_p, cd_pessoa_fisica_p    => cd_pessoa_fisica_p, nr_seq_ponto_acesso_p => nr_seq_ponto_acesso_p, qt_vagas_p            => qt_vagas_w, qt_ocupado_p          => qt_ocupado_w, qt_vagas_esp_p        => qt_vagas_esp_w, qt_ocupado_esp_p      => qt_ocupado_esp_w) INTO STRICT qt_vagas_p            => qt_vagas_w, qt_ocupado_p          => qt_ocupado_w, qt_vagas_esp_p        => qt_vagas_esp_w, qt_ocupado_esp_p      => qt_ocupado_esp_w;

    IF (ie_vaga_esp_w = 'S' AND qt_ocupado_esp_w >= qt_vagas_esp_w) OR
       (ie_vaga_esp_w = 'N' AND qt_ocupado_w >= qt_vagas_w) THEN
        ds_retorno_w := 'N';
    ELSE
        ds_retorno_w := 'S';

    END IF;

    RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
