CREATE OR REPLACE FUNCTION hd_obter_se_ponto_livre ( nr_seq_ponto_dialise_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		integer;
ie_retorno_w		varchar(1)	:= 'S';
ie_valida_w		varchar(1);

BEGIN

if (nr_seq_ponto_dialise_p IS NOT NULL AND nr_seq_ponto_dialise_p::text <> '') then
	select	max(ie_local_ponto)
	into STRICT	ie_valida_w
	from	hd_ponto_acesso a
	where	a.nr_sequencia = nr_seq_ponto_dialise_p;
end if;

if (coalesce(ie_valida_w,'N') not in ('R','M')) then
	select	count(*)
	into STRICT	qt_registro_w
	from	hd_maquina_dialise
	where	nr_seq_ponto	= nr_seq_ponto_dialise_p;
else
	qt_registro_w := 0;
end if;

if (qt_registro_w > 0) then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
