CREATE OR REPLACE FUNCTION hd_obter_se_priming_ideal ( nr_seq_modelo_p bigint, qt_priming_p bigint) RETURNS varchar AS $body$
DECLARE


qt_priming_fabrica_w		double precision;
pr_variacao_w			real;
qt_priming_menor_w		double precision;
qt_priming_maior_w		double precision;
ie_retorno_w			varchar(1);


BEGIN

/* Pega os dados do modelo do dialisador */

select	qt_priming_fabrica,
	pr_variacao
into STRICT	qt_priming_fabrica_w,
	pr_variacao_w
from	hd_modelo_dialisador
where	nr_sequencia	= nr_seq_modelo_p;

qt_priming_menor_w	:= qt_priming_fabrica_w - (qt_priming_fabrica_w * pr_variacao_w / 100);
qt_priming_maior_w	:= qt_priming_fabrica_w + (qt_priming_fabrica_w * pr_variacao_w / 100);

if (qt_priming_p >= qt_priming_menor_w) and (qt_priming_p <= qt_priming_maior_w) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
