SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_se_regra_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w		varchar(10);
nr_atendimento_w	bigint;
nr_seq_interno_w	bigint;
cd_convenio_w		bigint;
nr_seq_queixa_w		bigint;

ds_retorno_w		varchar(1)  := 'S';
			

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	count(*)
	into STRICT	qt_regra_w
	from	hd_regra_atend_dialise a
	where	ie_situacao = 'A';
	
	if (qt_regra_w > 0) then
		
		select	max(a.nr_seq_interno)
		into STRICT	nr_seq_interno_w
		from	atend_categoria_convenio a
		where	a.nr_atendimento = nr_atendimento_p;
		
		select	max(cd_convenio)
		into STRICT	cd_convenio_w
		from	atend_categoria_convenio a
		where	a.nr_seq_interno = nr_seq_interno_w;
		
		select	max(nr_seq_queixa)
		into STRICT	nr_seq_queixa_w
		from	atendimento_paciente a
		where	a.nr_atendimento = nr_atendimento_p;
		
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	hd_regra_atend_dialise a
		where	a.ie_situacao = 'A'
		and	a.cd_convenio = coalesce(cd_convenio_w, a.cd_convenio)
		and	nr_seq_queixa_w = coalesce(a.nr_seq_queixa,nr_seq_queixa_w);
		
	end if;	
	
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
