SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_se_regra_exame (ie_regra_exame_p text, cd_pessoa_fisica_p text, ie_job_p text default 'N') RETURNS varchar AS $body$
DECLARE


ie_diabetico_w		varchar(1);
nr_seq_tratamento_w	bigint;
dt_inicio_tratamento_w	timestamp;
dt_tipo_regra_w		timestamp;
ie_retorno_w		varchar(1);
ie_param_w		varchar(1);

BEGIN
ie_param_w := obter_param_usuario(7009, 213, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_w);

if (ie_param_w = 'S') or (ie_job_p = 'S') then

	select	coalesce(ie_diabetico,'N')
	into STRICT	ie_diabetico_w
	from	hd_pac_renal_cronico
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;


	select	min(nr_sequencia)
	into STRICT	nr_seq_tratamento_w
	from	paciente_tratamento
	where	coalesce(dt_final_tratamento::text, '') = ''
	and	ie_tratamento in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
	and	cd_pessoa_fisica = cd_pessoa_fisica_p;

	select	max(dt_inicio_tratamento)
	into STRICT	dt_inicio_tratamento_w
	from	paciente_tratamento
	where	nr_sequencia = nr_seq_tratamento_w;


	if (PKG_DATE_UTILS.start_of(clock_timestamp(), 'dd', 0) < PKG_DATE_UTILS.start_of(pkg_date_utils.add_month(dt_inicio_tratamento_w,12,0), 'dd', 0)) then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		
		where	((ie_regra_exame_p = 'G' and ie_diabetico_w = 'S') or (ie_regra_exame_p = 'T' and ie_diabetico_w = 'N'));
	else
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		
		where	((ie_regra_exame_p = 'R' and ie_diabetico_w = 'S') or (ie_regra_exame_p = 'A' and ie_diabetico_w = 'N'));
	end if;
	if (ie_diabetico_w = 'S') and (ie_regra_exame_p = 'D') then
		ie_retorno_w := 'S';
	end if;

else
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
