CREATE OR REPLACE FUNCTION hd_obter_se_seq_escala_dia ( cd_pessoa_fisica_p text, ie_dia_semana_p text, nr_seq_turno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_retorno_w		bigint;


BEGIN

if (ie_dia_semana_p IS NOT NULL AND ie_dia_semana_p::text <> '') and (nr_seq_turno_p IS NOT NULL AND nr_seq_turno_p::text <> '') then

	select  max(c.nr_sequencia)
	into STRICT	nr_seq_retorno_w
	from	hd_pac_renal_cronico a,
		hd_escala_dialise b,
		hd_escala_dialise_dia c
	where   	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	b.nr_sequencia = c.nr_seq_escala
	and	exists (	SELECT 	1
			from   	hd_escala_dialise_dia x
			where   	x.nr_seq_escala = b.nr_sequencia
			and    	x.ie_dia_semana = ie_dia_semana_p
			and	hd_obter_inicio_eslaca_dia(b.nr_sequencia, x.nr_seq_escala) <= clock_timestamp()
			and	coalesce(x.dt_fim_escala_dia::text, '') = ''
			and    	x.nr_seq_turno = nr_seq_turno_p);

	if (nr_seq_retorno_w IS NOT NULL AND nr_seq_retorno_w::text <> '') then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
