SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_se_tratamento_pac (cd_pessoa_fisica_p text, ie_tipo_tratamento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ie_retorno_w		varchar(1)	:= 'N';
qt_1_w			integer;
qt_2_w			integer;
qt_total_w		integer;

/*ie_opcao_p					
S - Paciente em tratamento
N - Se ja teve um tratamento

*/
BEGIN

select  count(*)
into STRICT	qt_1_w
from	paciente_tratamento
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	ie_tratamento		= ie_tipo_tratamento_p
and	ie_tipo_tratamento_p in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	(dt_inicio_tratamento IS NOT NULL AND dt_inicio_tratamento::text <> '')
and (coalesce(dt_final_tratamento::text, '') = '' or ie_opcao_p = 'N');

select count(*)
into STRICT	qt_2_w
from	paciente_tratamento
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	ie_tratamento		= ie_tipo_tratamento_p
and	ie_tipo_tratamento_p not in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	(dt_inicio_tratamento IS NOT NULL AND dt_inicio_tratamento::text <> '')
and (coalesce(dt_final_tratamento::text, '') = '' or ie_opcao_p = 'N');

qt_total_w := qt_1_w+qt_2_w;

if (qt_total_w > 0) then
	ie_retorno_w		:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
