CREATE OR REPLACE FUNCTION hd_obter_se_trat_ativo_agudo (cd_pessoa_fisica_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_agudo_w		varchar(1)	:= 'N';
					

BEGIN

select	coalesce(max(ie_paciente_agudo),'N')
into STRICT	ie_agudo_w
from	paciente_tratamento
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	ie_tratamento		in ('HD','PL','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	(dt_inicio_tratamento IS NOT NULL AND dt_inicio_tratamento::text <> '')
and (trunc(dt_inicio_tratamento) <= trunc(dt_referencia_p))
and	((coalesce(dt_final_tratamento::text, '') = '') or (dt_final_tratamento > dt_referencia_p));

return	ie_agudo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
