SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_status_tx (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_status_w	varchar(80);
ie_status_w	varchar(3);
ds_retorno_w	varchar(80);

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(substr(obter_valor_dominio(5125,ie_status),1,255)),
		max(ie_status)
	into STRICT	ds_status_w,
		ie_Status_w
	from	tx_encaminhamento a
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_inativacao::text, '') = ''
	and	dt_encaminhamento = (	SELECT	max(b.dt_encaminhamento)
					from	tx_encaminhamento b
					where	cd_pessoa_fisica = cd_pessoa_fisica_p
					and	coalesce(dt_inativacao::text, '') = '');

	if (ie_opcao_p = 'D') then
		ds_retorno_w := ds_status_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w := ie_status_w;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
