CREATE OR REPLACE FUNCTION hd_obter_tempo_dialise (cd_pessoa_fisica_p text, seq_grid_p bigint) RETURNS bigint AS $body$
DECLARE


qt_hora_sessao_w	smallint;
dt_referencia_w		timestamp;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (seq_grid_p IS NOT NULL AND seq_grid_p::text <> '') then
	select 	dt_referencia
	into STRICT	dt_referencia_w
	from	HD_EXAME_GRID_CONTROLE
	where 	nr_sequencia = seq_grid_p;

	select 	coalesce(round(sum((dt_fim_dialise - dt_inicio_dialise)*24)/count(*),2),0)
	into STRICT	qt_hora_sessao_w
	from	hd_dialise
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(dt_fim_dialise IS NOT NULL AND dt_fim_dialise::text <> '')
	and	(dt_inicio_dialise IS NOT NULL AND dt_inicio_dialise::text <> '')
	and	trunc(dt_dialise,'month') = trunc(dt_referencia_w,'month');
end if;

return qt_hora_sessao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
