CREATE OR REPLACE FUNCTION hd_obter_tempo_prescr_data (cd_pessoa_fisica_p text, seq_grid_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
QT_HORA_SESSAO_w	smallint;
QT_MIN_SESSAO_w		smallint;
dt_referencia_w		timestamp;



BEGIN

select 	dt_referencia
into STRICT	dt_referencia_w
from	HD_EXAME_GRID_CONTROLE
where 	nr_sequencia = seq_grid_p;

select 	round((sum(QT_HORA_SESSAO)/count(*))::numeric,2),
	round((sum(QT_MIN_SESSAO)/count(*))::numeric,2)
into STRICT	QT_HORA_SESSAO_w,
	QT_MIN_SESSAO_w
from	hd_prescricao a,
	prescr_medica b
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	a.nr_prescricao = b.nr_prescricao
and	trunc(dt_prescricao,'month') = trunc(dt_referencia_w,'month');

if (QT_HORA_SESSAO_w IS NOT NULL AND QT_HORA_SESSAO_w::text <> '') then
	ds_retorno_w	:=  QT_HORA_SESSAO_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
