SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_tratamento (cd_pessoa_fisica_p text, ie_separador_p text DEFAULT ',') RETURNS varchar AS $body$
DECLARE


    ds_tratamento varchar(2000) := '';
  i RECORD;

BEGIN
    FOR i IN (SELECT DISTINCT obter_valor_dominio(2197, pt.ie_tratamento) || ie_separador_p ds_tratamento
                FROM paciente_tratamento pt
               WHERE pt.cd_pessoa_fisica = cd_pessoa_fisica_p
                 AND clock_timestamp() BETWEEN pt.dt_inicio_tratamento AND coalesce(pt.dt_final_tratamento, clock_timestamp())) LOOP
        ds_tratamento := ds_tratamento || i.ds_tratamento;
    END LOOP;
    IF (ds_tratamento IS NOT NULL AND ds_tratamento::text <> '') THEN
        ds_tratamento := substr(ds_tratamento, 1, length(ds_tratamento) - length(ie_separador_p));
    END IF;

    RETURN ds_tratamento;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
