SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_turno_prc ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

-- C - Código
-- D - Descrição
-- G - Descrição (Recepção)
nr_seq_turno_w		bigint;
ds_retorno_w		varchar(255);
dt_inicio_w		timestamp;
ds_turno_w		varchar(255);
ie_dias_escala_w	varchar(1);

C01 CURSOR FOR
	SELECT	distinct substr(obter_descricao_padrao('HD_TURNO','DS_TURNO',d.NR_SEQ_TURNO),1,80)
	from	hd_escala_dialise a,
		hd_escala_dialise_dia d
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		d.dt_inicio_escala_dia <= clock_timestamp()
	and		a.nr_sequencia = d.nr_seq_escala
	and		coalesce(d.dt_fim_escala_dia::text, '') = ''
	order by 1;

BEGIN
ds_retorno_w	:= '';
ie_dias_escala_w := Obter_Param_Usuario(7009, 114, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_dias_escala_w);

if (ie_dias_escala_w = 'N') and (ie_opcao_p = 'G') then
	open C01;
	loop
	fetch C01 into
		ds_turno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w := ds_retorno_w ||' - '|| ds_turno_w;
		end;
	end loop;
	close C01;
	ds_retorno_w := substr(ds_retorno_w,4,255);
else

	select	max(nr_seq_turno)
	into STRICT	nr_seq_turno_w
	from	hd_escala_dialise
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	dt_inicio <= clock_timestamp()
	and	coalesce(dt_fim::text, '') = '';
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_seq_turno_w;
elsif (ie_opcao_p = 'D') or
	(ie_opcao_p = 'G' AND ie_dias_escala_w = 'S')then
	select	substr(ds_turno,1,200)
	into STRICT	ds_retorno_w
	from	hd_turno
	where	nr_sequencia	= nr_seq_turno_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
