CREATE OR REPLACE FUNCTION hd_obter_ultima_infusao ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		bigint;
cd_pessoa_fisica_w	bigint;
dt_dialise_w		timestamp;
hr_dialise_w		timestamp;
hr_dialise_peritonial_w	timestamp;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	cd_pessoa_fisica,
		dt_dialise_peritonial,
		hr_dialise_peritonial
	into STRICT	cd_pessoa_fisica_w,
		dt_dialise_w,
		hr_dialise_peritonial_w
	from	hd_dialise_peritonial
	where	nr_sequencia = nr_sequencia_p;

	select	max(hr_dialise_peritonial)
	into STRICT	hr_dialise_w
	from	hd_dialise_peritonial
	where	nr_sequencia <> nr_sequencia_p
	and	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	dt_dialise_peritonial = dt_dialise_w
	and	to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||''||to_char(hr_dialise_peritonial,'hh24:mi'),'dd/mm/yyyy hh24:mi') <=
		to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||''||to_char(hr_dialise_peritonial_w,'hh24:mi'),'dd/mm/yyyy hh24:mi');

	select	max(qt_volume_infusao)
	into STRICT	vl_retorno_w
	from	hd_dialise_peritonial
	where	nr_sequencia <> nr_sequencia_p
	and	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	dt_dialise_peritonial = dt_dialise_w
	and	hr_dialise_peritonial = hr_dialise_w;
end if;

return	vl_retorno_w;

end;
$body$
