CREATE OR REPLACE FUNCTION hd_obter_ultimo_atend_paciente (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	bigint;


BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	atendimento_paciente a
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.ie_fim_conta <> 'F'
and	coalesce(a.dt_cancelamento::text, '') = ''
and	exists (SELECT 1 from hd_log_geracao_atend x where a.nr_atendimento = x.nr_atendimento_nov)
and	coalesce(a.dt_alta::text, '') = '';


if (coalesce(nr_atendimento_w::text, '') = '') then

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	atendimento_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_fim_conta <> 'F'
	and	coalesce(dt_cancelamento::text, '') = ''
	and	coalesce(dt_alta::text, '') = '';

end if;

return	coalesce(nr_atendimento_w,0);

end;
