CREATE OR REPLACE FUNCTION hd_obter_ultimo_trat_perit ( cd_pessoa_fisica_p text, dt_parametro_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(15);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select 	ie_tratamento
	into STRICT 	ds_retorno_w
	from (SELECT ie_tratamento,
	  		dt_inicio_tratamento
		from	paciente_tratamento
		where  	cd_pessoa_fisica = cd_pessoa_Fisica_p
		and	dt_inicio_tratamento <= last_day(fim_dia(dt_parametro_p))
		and	ie_tratamento in ('CAPD','CPI','DPA', 'DPAV')
		order by dt_inicio_tratamento desc) alias4 LIMIT 1;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
